/*Desafio: obter a maior venda de um produto*/
SELECT TOP (1) * FROM dbo.ITENS_NOTAS_FISCAIS INNER JOIN dbo.TABELA_DE_PRODUTOS ON dbo.ITENS_NOTAS_FISCAIS.CODIGO_DO_PRODUTO = dbo.TABELA_DE_PRODUTOS.CODIGO_DO_PRODUTO WHERE (TABELA_DE_PRODUTOS.NOME_DO_PRODUTO = 'Linha Refrescante - 1 Litro - Morango/Limao') ORDER BY QUANTIDADE DESC